REPLACE INTO `modx_site_htmlsnippets` VALUES (17,1,0,'tpl.msOptions','Чанк вывода дополнительных свойств товара miniShop2.',0,8,0,'{foreach $options as $name => $values}\r\n    <div class=\"form-group row align-items-center\">\r\n        <label class=\"col-6 col-md-3 text-right text-md-left col-form-label\" for=\"option_{$name}\">{(\'ms2_product_\' ~ $name) | lexicon}:</label>\r\n        <div class=\"col-6 col-md-9\">\r\n            <select name=\"options[{$name}]\" class=\"form-control col-md-6\" id=\"option_{$name}\">\r\n                {foreach $values as $value}\r\n                    <option value=\"{$value}\">{$value}</option>\r\n                {/foreach}\r\n            </select>\r\n        </div>\r\n    </div>\r\n{/foreach}\r\n',0,NULL,0,'core/components/minishop2/elements/chunks/chunk.ms_options.tpl');
